using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using System.Numerics;
using MathLibrary;

namespace Binäärilaskukone
{
    class Program
    {
        static void Main(string[] args)
        {
            RegistryHandler rh = new RegistryHandler();
            //rh.addAnswer(666);
            MathClass MC = new MathClass();
            if (args.Count() != 0)
            {
                if (args[0] == "-b")
                {
                    try
                    {
                        BigInteger result = MC.binaryToDecimal(args[1]);
                        if (result == 666)
                        {
                            Console.ForegroundColor = ConsoleColor.Red;
                            // pitäisikö palautus tulla suoraan stringinä, niin voisi palauttaa exceptionin sieltä?
                            Console.WriteLine("Not in valid Binary format!");
                            Console.ResetColor();
                        }
                        else
                        {
                            Console.WriteLine("Vastaus:" + result);
                            rh.addAnswer(result.ToString());
                        }
                    }
                    catch (Exception ex)
                    {
                        Console.WriteLine(ex);
                    }
                    Environment.Exit(0);
                }

                else if (args[0] == "-c")
                {
                    try 
                    {
                        decimal value = Convert.ToDecimal(args[1]);
                        string answer = MC.Convert(value, args[2], args[3]);
                        Console.WriteLine(answer);
                        rh.addAnswer(answer);
                    }
                    catch (Exception ex) 
                    { }
                }
            }
            else
            {
                while (true)
                {
                    Console.Clear();
                    Console.WriteLine("Binaarilaskukone - Tomi Saari & Jukka Nurminen");
                    Console.WriteLine("1 - Binäärimuunnin\n2 - Valuuttamuunin\n3 - Luonnollisten lukujen summa\n4 - Potenssisummien erotus\n5 - 10001:n alkuluku\n6 - Viimeisin vastaus rekisteristä\n0 - Poistu");
                    Console.WriteLine();
                    Console.WriteLine("Valinta: ");
                    string valinta = Console.ReadLine();

                    // RegistryClass rc = new RegistryClass();
                    // string returnMessage = rc.printSomething("Hello World!");
                    //Console.WriteLine("Return message: " + returnMessage);

                    if (valinta == "1")
                    {
                        string convertThis = "";
                        Console.WriteLine("Anna binääriluku: ");
                        convertThis = Console.ReadLine();
                        BigInteger result = MC.binaryToDecimal(convertThis);
                        if (result == 666)
                        {
                            Console.ForegroundColor = ConsoleColor.Red;
                            // pitäisikö palautus tulla suoraan stringinä, niin voisi palauttaa exceptionin sieltä?
                            Console.WriteLine("Not in valid Binary format!");
                            Console.ResetColor();
                        }
                        else
                        {
                            Console.WriteLine("Vastaus:" + result);
                            rh.addAnswer(result.ToString());
                        }

                    }
                    else if (valinta == "2")
                    {
                        Console.WriteLine("Anna summa: ");
                        string sumString = Console.ReadLine();
                        decimal sum = 0;
                        try
                        {
                            sum = Convert.ToDecimal(sumString);
                        }
                        catch (Exception ex)
                        {
                            Console.WriteLine(ex);
                        }
                        Console.WriteLine("Anna valuutta mistä muunnetaan (Esim: usd): ");
                        string from = Console.ReadLine();
                        Console.WriteLine("Anna valuutta mihin muunnetaan (Esim: eur): ");
                        string to = Console.ReadLine();
                        string temp = MC.Convert(sum, from, to);
                        Console.WriteLine(temp);
                        rh.addAnswer(temp);

                    }
                    else if (valinta == "3")
                    {
                        int temp = MC.SumOfNaturals(1000);
                        rh.addAnswer(temp.ToString());
                        Console.WriteLine(temp);
                        //Console.WriteLine(MC.SumOfNaturals(1000));
                    }
                    // poistettu, koska todella pitkä laskuaika
                    else if (valinta == "4")
                    {
                        int pe = MC.PotenssisummienErotus();
                        Console.WriteLine(pe);
                        rh.addAnswer(pe.ToString());
                    }
                    else if (valinta == "5")
                    {
                        //MC.Alkuluku();
                        rh.addAnswer(MC.Alkuluku().ToString());
                        Console.ResetColor();

                    }
                    else if (valinta == "6")
                    {
                        Console.WriteLine("Viimeisin vastaus: " + rh.readAnswer());
                    }
                    else if (valinta == "0")
                    {
                        Environment.Exit(0);
                    }
                    else
                    {
                        Console.WriteLine("Tuntematon valinta!");
                    }
                    Console.ReadLine();
                }
                //Console.WriteLine(result.);
            }
        }
    }
}
